#!/bin/bash

set -e

pushd `pwd`

if [ "$(basename "$PWD")" = "scripts/dev" ]; then
  cd ../..
fi

. .env

if [[ "$(dfx canister bot 2>&1 | grep Error)" != "" ]]; then
  dfx canister create bot
fi
if [[ "$(dfx canister minter 2>&1 | grep Error)" != "" ]]; then
  dfx canister create minter
fi
if [[ "$(dfx canister coin 2>&1 | grep Error)" != "" ]]; then
  dfx canister create coin
fi

./scripts/utils/download-dfinity-wasm.sh icrc1_ledger ic-icrc1-ledger

BOT_CANISTER_ID=$(dfx canister id bot)
MINTER_CANISTER_ID=$(dfx canister id minter)

dfx deploy coin -v --identity default --with-cycles 10000000000000 --argument "(variant {Init = record{
  minting_account= record {
    owner = principal \"$MINTER_CANISTER_ID\";
    subaccount = null;
  };
  fee_collector_account = opt record {
    owner = principal \"$BOT_CANISTER_ID\";
    subaccount = null;
  };
  transfer_fee = 100000;
  decimals = opt 8;
  max_memo_length = opt 64;
  token_symbol = \"MEME\";
  token_name = \"Meme Coin\";
  metadata = vec { 
    record {
        \"icrc1:logo\";
        variant {
            Text = \"data:image/png;base64,\"
        };
    };
  };
  initial_balances = vec { };
  feature_flags = opt record {
    icrc2 = true;
  };
  archive_options = record {
    num_blocks_to_archive = 1000;
    max_transactions_per_response = opt 64;
    trigger_threshold = 2000;
    max_message_size_bytes = null;
    cycles_for_archive_creation = opt 1_000_000_000_000;
    node_max_memory_size_bytes = null;
    controller_id = principal \"$BOT_CANISTER_ID\";
    more_controller_ids = null;
  }
}})"

####
dfx canister update-settings coin --add-controller $BOT_CANISTER_ID

####
COIN_CANISTER_ID=$(dfx canister id coin)

dfx --identity default canister call registry add_token "(record {
    ledger_canister_id = principal \"$COIN_CANISTER_ID\";
    payer = null;
    token_standard = variant { icrc1 };
    info_url = \"https://dashboard.internetcomputer.org/sns/$COIN_CANISTER_ID\";
    transaction_url_format = \"https://dashboard.internetcomputer.org/sns/$COIN_CANISTER_ID/transactions/{transaction_index}\";
})"

popd